import React, { useContext, useEffect, useState } from "react";
import styles from "./infokipkuliah.module.css";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import { KIPLogo } from "../../img";
import apiClient from "../../../api/apiClient";

const InfoKIPKuliah = () => {
  const navigate = useNavigate();
  const { isLoggedIn, user } = useContext(AuthContext);

  const [isRegistered, setIsRegistered] = useState(false);
  const [scholarshipType, setScholarshipType] = useState("");
  const [isActive, setIsActive] = useState(false);
  const [loadingStatus, setLoadingStatus] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [alertMessage, setAlertMessage] = useState(null);

  useEffect(() => {
    const fetchScholarshipStatus = async () => {
      try {
        const res = await apiClient.get(`/scholarshipType/detail?name=KIP%20Kuliah`);
        const detail = res.data?.output_schema?.result;
        setIsActive(detail?.active ?? false);
      } catch (err) {
        console.error("Gagal memuat status beasiswa:", err);
      }
    };

    const fetchRegistrationDetail = async () => {
      const storedUser = JSON.parse(localStorage.getItem("user"));
      if (!storedUser) return;

      const userUuid = storedUser.user?.uuid;

      if (isLoggedIn && userUuid) {
        try {
          const res = await apiClient.get(`/scholarship/detail?userUuid=${userUuid}`);
          const detail = res.data?.output_schema?.result;

          if (detail && Object.keys(detail).length > 0) {
            setIsRegistered(true);
            setScholarshipType(detail.scholarship_type || "");
          }
        } catch (error) {
          console.error("Gagal memuat detail pendaftaran:", error);
        } finally {
          setLoadingStatus(false);
        }
      } else {
        setLoadingStatus(false);
      }
    };

    fetchScholarshipStatus();
    fetchRegistrationDetail();
  }, [isLoggedIn, user]);

  const handleRegisterClick = () => {
    if (!isActive) {
      setAlertMessage("Pendaftaran Beasiswa KIP Kuliah saat ini tidak aktif.");
      return;
    }

    if (isLoggedIn) {
      if (!isRegistered) {
        navigate("/beasiswa/kipkuliah/form-pendaftaran");
      } else {
        setShowModal(true);
      }
    } else {
      navigate("/login");
    }
  };

  const closeModal = () => setShowModal(false);
  const closeAlert = () => setAlertMessage(null);

  return (
    <div className={styles.container}>
      <main className={styles.mainContent}>
        <section className={styles.headerSection}>
          <div className={styles.headerTop}>
            <img src={KIPLogo} alt="Logo KIP" className={styles.programLogo} />
            <h2 className={styles.mainTitle}>KIP Kuliah Merdeka</h2>
          </div>

          <div className={styles.headerRight}>
            <button
              onClick={handleRegisterClick}
              className={`${styles.registerButton} ${!isActive ? styles.inactiveButton : ""}`}
              disabled={!isActive}
            >
              {isActive ? "Daftar" : "Beasiswa Tidak Aktif"}
            </button>
          </div>

          {/* Modal: Sudah terdaftar */}
          {showModal && (
            <div className={styles.modalOverlay}>
              <div className={styles.modalContent}>
                <p>
                  Anda sudah terdaftar pada Beasiswa <strong>{scholarshipType}</strong>.
                </p>
                <p>Anda tidak dapat mendaftar beasiswa lain.</p>
                <button onClick={closeModal} className={styles.closeButton}>
                  Tutup
                </button>
              </div>
            </div>
          )}

          {/* Modal: Beasiswa tidak aktif */}
          {alertMessage && (
            <div className={styles.modalOverlay}>
              <div className={styles.modalContent}>
                <p>{alertMessage}</p>
                <button onClick={closeAlert} className={styles.closeButton}>
                  Tutup
                </button>
              </div>
            </div>
          )}
        </section>

        <section className={styles.requirementsSection}>
          <h3 className={styles.requirementsTitle}>
            Persyaratan Penerimaan KIP Kuliah Merdeka 2022
          </h3>
          <div className={styles.requirementsContent}>
            <p>
              1. Penerima KIP Kuliah Merdeka adalah lulusan SMA/SMK/sederajat yang lulus pada
              tahun berjalan atau maksimal lulus dua tahun sebelumnya.
            </p>
            <p>
              2. Telah lulus seleksi masuk Perguruan Tinggi dan diterima di program studi
              terakreditasi.
            </p>
            <p>
              3. Memiliki potensi akademik baik tetapi keterbatasan ekonomi, dibuktikan dengan:
            </p>
            <ul>
              <li>Kartu Indonesia Pintar (KIP)</li>
              <li>Peserta Program Keluarga Harapan (PKH)</li>
              <li>Pemegang Kartu Keluarga Sejahtera (KKS)</li>
              <li>Dari panti asuhan/panti sosial</li>
              <li>Masuk dalam DTKS Kemensos</li>
            </ul>
            <p>
              Jika tidak memenuhi kriteria di atas, dapat mendaftar bila pendapatan orang tua
              ≤ Rp4.000.000 atau ≤ Rp750.000 per anggota keluarga.
            </p>
            <p>
              Manfaat: pembebasan biaya kuliah, bantuan biaya hidup (Rp800.000–Rp1.400.000/bulan),
              dan bebas biaya UTBK.
            </p>
            <p>Durasi: 8 semester (S1), 6 semester (D3), dst.</p>
          </div>
        </section>
      </main>
    </div>
  );
};

export default InfoKIPKuliah;
