import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import apiClient from "../../../../../api/apiClient";

const AnnouncementUpdate = () => {
  const { uuid } = useParams();
  const [announcement, setAnnouncement] = useState({});
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAnnouncement = async () => {
      try {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        const token = storedUser?.token;

        const response = await apiClient.get(`/announcement/detail?uuid=${uuid}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        const data = response.data?.result || {};
        setAnnouncement(data);
      } catch (error) {
        console.error("Error fetching announcement:", error);
      }
    };

    fetchAnnouncement();
  }, [uuid]);

  const handleUpdate = async () => {
    try {
      setLoading(true);

      const storedUser = JSON.parse(localStorage.getItem("user"));
      const token = storedUser?.token;

      await apiClient.put("/announcement/update", announcement, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert("Perubahan berhasil disimpan!");
      navigate("/bidang/announcement");
    } catch (error) {
      console.error("Error updating announcement:", error);
      alert("Gagal memperbarui pengumuman.");
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setAnnouncement({ ...announcement, [name]: value });
  };

  return (
    <section className="container py-5">
      <div className="row">
        <div className="col-lg-8 mx-auto">
          <div className="card shadow-lg border-0 rounded-4 p-4">
            <div className="card-body">
              <h2 className="h4 fw-bold text-primary mb-4 text-center">
                Update Pengumuman
              </h2>

              <form>
                {/* Judul */}
                <div className="mb-3">
                  <label htmlFor="title" className="form-label">
                    Judul Pengumuman
                  </label>
                  <input
                    type="text"
                    id="title"
                    name="title"
                    className="form-control"
                    value={announcement.title || ""}
                    onChange={handleInputChange}
                  />
                </div>

                {/* Kategori (dropdown) */}
                <div className="mb-3">
                  <label htmlFor="category" className="form-label">
                    Kategori
                  </label>
                  <select
                    id="category"
                    name="category"
                    className="form-select"
                    value={announcement.category || ""}
                    onChange={handleInputChange}
                  >
                    <option value="">-- Pilih Kategori --</option>
                    <option value="GenBI">GenBI</option>
                    <option value="KIP Kuliah">KIP Kuliah</option>
                  </select>
                </div>

                {/* Tanggal Terbit */}
                <div className="mb-3">
                  <label htmlFor="publish_date" className="form-label">
                    Tanggal Terbit
                  </label>
                  <input
                    type="date"
                    id="publish_date"
                    name="publish_date"
                    className="form-control"
                    value={announcement.publish_date || ""}
                    onChange={handleInputChange}
                  />
                </div>

                {/* Tanggal Tayang */}
                <div className="mb-3">
                  <label htmlFor="display_date" className="form-label">
                    Tanggal Tayang
                  </label>
                  <input
                    type="date"
                    id="display_date"
                    name="display_date"
                    className="form-control"
                    value={announcement.display_date || ""}
                    onChange={handleInputChange}
                  />
                </div>

                {/* Deskripsi */}
                <div className="mb-3">
                  <label htmlFor="description" className="form-label">
                    Deskripsi
                  </label>
                  <textarea
                    id="description"
                    name="description"
                    className="form-control"
                    rows="5"
                    value={announcement.description || ""}
                    onChange={handleInputChange}
                  />
                </div>

                {/* Tombol */}
                <div className="text-end">
                  <button
                    type="button"
                    className="btn btn-primary px-4"
                    onClick={handleUpdate}
                    disabled={loading}
                  >
                    {loading ? "Menyimpan..." : "Simpan Perubahan"}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default AnnouncementUpdate;
